mapper.delete:
DELETE
FROM application_form af
WHERE af.`activityId` = #{activityId};
DELETE
FROM volunteer_list vl
WHERE vl.`activityId` = #{activityId};
DELETE
FROM activity a
WHERE a.`id` = #{activityId};

mapper.update:
UPDATE activity a
SET a.restNum = #{restNum}
WHERE a.id = #{activityId}

UPDATE activity a
SET a.`summary` = #{summary}
WHERE a.`id` = #{activityId}

mapper.select:
SELECT a.`signTime`
FROM activity a
WHERE a.`id` = #{activityId}

mapper.update:
UPDATE activity a
SET a.signTime = #{signTime}
WHERE a.`id` = #{activityId}

mapper.select:
SELECT *
FROM activity a
WHERE a.`id` = #{activityId}
